var http=require("http"),hyperquest=require("../"),server=http.createServer(function(e,t){t.write(e.url.slice(1)+"\n");setTimeout(t.end.bind(t),3e3)});server.listen(5e3,function(){var e=20;for(var t=0;t<20;t++){var n=hyperquest("http://localhost:5000/"+t);n.pipe(process.stdout,{end:!1});n.on("end",function(){--e===0&&server.close()})}});process.stdout.setMaxListeners(0);