function check(e,t){var n=hyperquest("http://localhost:"+t),r="";n.pipe(through(function(e){r+=e}));setTimeout(function(){e.equal(r,"beep boop")},100);return n}var test=require("tap").test,http=require("http"),hyperquest=require("../"),through=require("through"),server=http.createServer(function(e,t){t.write("beep boop")});test("more than 5 pending connections",function(e){e.plan(20);var t=[];server.listen(0,function(){var n=server.address().port;for(var r=0;r<20;r++)t.push(check(e,n))});e.on("end",function(){t.forEach(function(e){e.destroy()});server.close()})});