function check(e,t){var n=hyperquest.post("http://localhost:"+t);n.end("beep boop.");var r="";n.on("data",function(e){r+=e});n.on("end",function(){e.equal(r,"BEEP BOOP.")})}var test=require("tap").test,http=require("http"),hyperquest=require("../"),through=require("through"),server=http.createServer(function(e,t){e.pipe(through(function(e){this.queue(String(e).toUpperCase())})).pipe(t)});test("post",function(e){e.plan(1);server.listen(0,function(){var n=server.address().port;check(e,n)});e.on("end",server.close.bind(server))});