var isStream=require("../lib/isStream"),Stream=require("stream"),should=require("should");require("mocha");describe("isStream()",function(){it("should return true on a Stream",function(e){var t=new Stream;isStream(t).should.equal(!0);e()});it("should return false on a Buffer",function(e){var t=new Buffer("test");isStream(t).should.equal(!1);e()});it("should return false on a null",function(e){isStream(null).should.equal(!1);e()});it("should return false on a array of numbers",function(e){var t=[1,2,3];isStream(t).should.equal(!1);e()})});