var util=require("../"),should=require("should"),path=require("path");require("mocha");describe("File()",function(){it("should return a valid file",function(e){var t=path.join(__dirname,"./fixtures/test.coffee"),n=path.join(__dirname,"./fixtures/"),r=new util.File({base:n,cwd:__dirname,path:t});should.exist(r,"root");should.exist(r.relative,"relative");should.exist(r.path,"path");should.exist(r.cwd,"cwd");should.exist(r.base,"base");r.path.should.equal(t);r.cwd.should.equal(__dirname);r.base.should.equal(n);r.relative.should.equal("test.coffee");e()});it("should return a valid file 2",function(e){var t=path.join(__dirname,"./fixtures/test.coffee"),n=__dirname,r=new util.File({base:n,cwd:__dirname,path:t});should.exist(r,"root");should.exist(r.relative,"relative");should.exist(r.path,"path");should.exist(r.cwd,"cwd");should.exist(r.base,"base");r.path.should.equal(t);r.cwd.should.equal(__dirname);r.base.should.equal(n);r.relative.should.equal("fixtures/test.coffee");e()})});