var util=require("../"),should=require("should"),path=require("path"),es=require("event-stream"),Stream=require("stream");require("mocha");describe("combine()",function(){it("should return a function",function(e){var t=[1,2,3],n=es.readArray(t),r=util.combine(n);r.should.be.type("function");e()});it("should return a function that returns a stream combination",function(e){var t=[1,2,3],n=es.readArray(t),r=es.writeArray(function(n,r){should.not.exist(n);r.should.eql(t);e()}),i=util.combine(n,r);i().should.be.instanceof(Stream)});it("should return a function that returns a stream combination when given an array",function(e){var t=[1,2,3],n=es.readArray(t),r=es.writeArray(function(n,r){should.not.exist(n);r.should.eql(t);e()}),i=util.combine([n,r]);i().should.be.instanceof(Stream)})});