var util=require("../"),should=require("should"),path=require("path");require("mocha");describe("date",function(){it("should be a date format instance",function(e){util.date.should.equal(require("dateformat"));e()});it("should return today's date",function(e){var t=new Date,n=util.date("HH:MM:ss");n.should.equal(("0"+t.getHours()).slice(-2)+":"+("0"+t.getMinutes()).slice(-2)+":"+("0"+t.getSeconds()).slice(-2));e()})});