var util=require("../"),should=require("should"),path=require("path"),through=require("through2");require("mocha");describe("isNull()",function(){it("should work on a null",function(e){util.isNull(null).should.equal(!0);e()});it("should not work on a stream",function(e){util.isNull(through()).should.equal(!1);e()});it("should not work on undefined",function(e){util.isNull(undefined).should.equal(!1);e()})});