var util=require("../"),should=require("should"),path=require("path");require("mocha");describe("replaceExtension()",function(){it("should return a valid replaced extension on nested",function(e){var t=path.join(__dirname,"./fixtures/test.coffee"),n=path.join(__dirname,"./fixtures/test.js"),r=util.replaceExtension(t,".js");should.exist(r);r.should.equal(n);e()});it("should return a valid replaced extension on flat",function(e){var t="test.coffee",n="test.js",r=util.replaceExtension(t,".js");should.exist(r);r.should.equal(n);e()});it("should not return a valid replaced extension on empty string",function(e){var t="",n="",r=util.replaceExtension(t,".js");should.exist(r);r.should.equal(n);e()})});