var glob2base=require("../"),glob=require("glob"),path=require("path"),should=require("should");require("mocha");describe("glob2base",function(){it("should get a base name",function(){var e=new glob.Glob("js/*.js",{cwd:__dirname});glob2base(e).should.equal("js/")});it("should get a base name from a nested glob",function(){var e=new glob.Glob("js/**/test/*.js",{cwd:__dirname});glob2base(e).should.equal("js/")});it("should get a base name from a flat file",function(){var e=new glob.Glob("js/test/wow.js",{cwd:__dirname});glob2base(e).should.equal("js/test/")});it("should get a base name from character class pattern",function(){var e=new glob.Glob("js/t[a-z]st}/*.js",{cwd:__dirname});glob2base(e).should.equal("js/")});it("should get a base name from brace , expansion",function(){var e=new glob.Glob("js/{src,test}/*.js",{cwd:__dirname});glob2base(e).should.equal("js/")});it("should get a base name from brace .. expansion",function(){var e=new glob.Glob("js/test{0..9}/*.js",{cwd:__dirname});glob2base(e).should.equal("js/")});it("should get a base name from extglob",function(){var e=new glob.Glob("js/t+(wo|est)/*.js",{cwd:__dirname});glob2base(e).should.equal("js/")});it("should get a base name from a complex brace glob",function(){var e=new glob.Glob("lib/{components,pages}/**/{test,another}/*.txt",{cwd:__dirname});glob2base(e).should.equal("lib/components/");var t=new glob.Glob("js/test/**/{images,components}/*.js",{cwd:__dirname});glob2base(t).should.equal("js/test/")})});