var cloneBuffer=require("../lib/cloneBuffer"),Stream=require("stream"),should=require("should");require("mocha");describe("cloneBuffer()",function(){it("should return a new Buffer reference",function(e){var t=new Buffer("test"),n=cloneBuffer(t);should.exist(n,"should return something");(n instanceof Buffer).should.equal(!0,"should return a Buffer");n.should.not.equal(t,"pointer should change");e()});it("should not replicate modifications to the original buffer",function(e){var t=new Buffer("test"),n=cloneBuffer(t);n.write("w");t.toString("utf8").should.equal("test","original should stay the same");n.toString("utf8").should.equal("west","new buffer should be modified");e()})});