var isNull=require("../lib/isNull"),Stream=require("stream"),should=require("should");require("mocha");describe("isNull()",function(){it("should return true on null",function(e){isNull(null).should.equal(!0);e()});it("should return false on undefined",function(e){isNull().should.equal(!1);isNull(undefined).should.equal(!1);e()});it("should return false on defined values",function(e){isNull(1).should.equal(!1);isNull("test").should.equal(!1);e()})});